<?php//var_dump($queue);echo form_open(base_url('tweetmessage/save'));echo form_hidden('itemid',$queue['id']);	echo form_hidden('com_autofbtwitter_queue_id',$queue['id']);	echo form_hidden('id',$message['id']);	echo form_hidden('scheduled_date_time_server_time',$message['scheduled_date_time_server_time']);?><?phpif(empty($message['id'])){	echo "<h3>Add tweet message</h3>";}else{	echo "<h3>Edit tweet message</h3>";}?><fieldset>	<dl>	            		<dt>Queue Name:</dt>		<dd><?php echo $queue['name']?></dd>	</dl>	<dl>	            		<dt>Scheduled Time</dt>		<dd><?php echo $message['start_date'].' '. $message['start_hhmm']?></dd>	</dl>			<dl>	            		<dt>Message</dt>		<dd><textarea name="message" id="message"><?php echo $message['message'];?></textarea></dd>	</dl>	<dl>		<dt>&nbsp;</dt>		<dd>						<input type="button" id="btnImgUpload" value="Add Image" />			<input type="button" id="btnShortLink" value="Tiny Links" />		</dd>			</dl>	<dl>		<dt><label>&nbsp;</label></dt>		<dd><?php echo form_submit('submit', 'Save');?>		<?php echo form_button('cancel', 'Cancel', 'onClick="window.location=\''.base_url('tweetqueue/index').'\'"'); ?></dd>	</dl>	<input type="hidden" id="username" value="<?php echo $queue['com_autofbtwitter_account_id'];?>" />	</fieldset><div id="upload-form"></div><?phpecho form_close();?><div style="clear:both"></div>	<?phpecho $table;?><script type="text/javascript">jQuery(document).ready(function() {	$( "#upload-form" ).dialog({		autoOpen: false,		height: 200,		width: 400,		modal: true,		buttons: {				Ok: function() {										$( this ).dialog( "close" );				}				,				Cancel: function() {					$( this ).dialog( "close" );				}		},		close: function() {                    		},		open:function(){                						$( "#upload-form" ).load('<?php echo base_url('upload/index');?>',function(){				$('#upload_file').submit(function(e) {					$('#files').html('<img src="<?php echo base_url('images/ajax-loader.gif');?>" /><span>Uploading file...</span>'); 				  e.preventDefault();				  $.ajaxFileUpload({					 url         :'<?php echo base_url('tweetmessage/upload_file');?>',					 secureuri      :false,					 fileElementId  :'userfile',					 dataType    : 'json',					 data        : {						<?php echo $this->security->get_csrf_token_name();?>:'<?php echo $this->security->get_csrf_hash();?>',						'params':$('#username').val()					 },					 success  : function (data, status)					 {						if(data.status != 'error')						{						   $('#files').html('');						   var temp = $('#message').val();						   $('#message').val(temp+' '+data.msg);						   $( "#upload-form" ).dialog( "close" );						}else{							alert(data.msg);						}					 }				  });				  return false;			   });			});                		}	});	$( "#btnImgUpload" )            		.click(function() {				$( "#upload-form" ).dialog( "open" );	});	$('#btnShortLink').click(function(){				$.post(			'<?php echo base_url('tweetmessage/shorten_links');?>',			{				text:$('#message').val(),				<?php echo $this->security->get_csrf_token_name();?>:'<?php echo $this->security->get_csrf_hash();?>'			},			function(data){				if(data!=null)				{					$('#message').val(jQuery.trim(data));				}			}		);	});});</script>